INSERT INTO `posts` VALUES (32, 'KhiênG và Box Gaming \"khóc một dòng sông\", Saigon Phantom lật kèo mãn nhãn nhờ nội tại \"linh vật\" Huy Popper', 'Màn lật kèo của Saigon Phantom trước Box Gaming sau khi để thua 2 ván đang là chủ đề hot nhất của làng Liên Quân hiện tại. Đồng thời trận đấu này cũng đánh dấu sự trở lại của một huyền thoại dự đoán mang tên Huy Popper.', '<p style=\"margin-bottom: 25px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 17px; line-height: 25px; font-family: &quot;Times New Roman&quot;, Georgia, serif; vertical-align: baseline; color: rgb(34, 34, 34); -webkit-font-smoothing: subpixel-antialiased;\"><span style=\"margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline;\">Ra sân với đội hình không phải mạnh nhất bởi sự thiếu vắng BronzeV nên SGP không thể triển khai lối đánh áp đảo từ sớm quen thuộc của mình. Ngược lại, Box Gaming lại làm vô cùng tốt trong 2 ván đấu đầu tiên mà người xuất sắc chính là KhiênG với những tình huống giao tranh cực kỳ hợp lý.</span></p><p style=\"margin-bottom: 25px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 17px; line-height: 25px; font-family: &quot;Times New Roman&quot;, Georgia, serif; vertical-align: baseline; color: rgb(34, 34, 34); -webkit-font-smoothing: subpixel-antialiased;\"><span style=\"margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline;\">Box Gaming giành được liên tiếp 2 ván thắng và đứng trước cơ hội đè bẹp SGP đang trên đà thăng hoa.</span><br></p><div class=\"VCSortableInPreviewMode\" type=\"Photo\" style=\"margin: 0px auto 25px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 17px; line-height: inherit; font-family: &quot;Times New Roman&quot;, Georgia, serif; vertical-align: baseline; outline: 0px; text-al