INSERT INTO `posts` VALUES (35, 'Call of Duty: Mobile VN tặng anh em game thủ 2000 code VIP', 'Call of Duty: Mobile VN ra mắt đã thực sự \"đốt nóng\" làng game Việt trong những ngày qua. Ngoài các sự kiện miễn phí, chương trình ưu đãi đặc biệt thì việc tặng code VIP cho người chơi đang trở thành cơn sốt nóng hừng hực trong lòng cộng đồng.', '<p style=\"margin-bottom: 0px; padding: 0px 0px 20px; list-style: none; border: 0px; font-size: 17px; outline: none; color: rgb(51, 51, 51); font-family: &quot;Times New Roman&quot;;\"><span style=\"margin: 0px; padding: 0px; list-style: none; border: 0px; outline: none;\">Theo đó, code VIP chứa Rương Thi Đấu và C, giúp game thủ thuận tiện hơn trong việc trải nghiệm Call of Duty: Mobile VN.</span></p><p style=\"margin-bottom: 0px; padding: 0px 0px 20px; list-style: none; border: 0px; font-size: 17px; outline: none; color: rgb(51, 51, 51); font-family: &quot;Times New Roman&quot;;\"><span style=\"margin: 0px; padding: 0px; list-style: none; border: 0px; outline: none;\">Lưu ý là mỗi tài khoản chỉ nhập được code này một lần, hạn sử dụng của code đến 31/8/2020.</span><br style=\"margin: 0px; padding: 0px; list-style: none;\"></p><p style=\"margin-bottom: 0px; padding: 0px 0px 20px; list-style: none; border: 0px; font-size: 17px; outline: none; color: rgb(51, 51, 51); font-family: &quot;Times New Roman&quot;;\"><font color=\"#ff0000\" style=\"margin: 0px; padding: 0px; list-style: none; border: 0px; outline: none;\"><b style=\"margin: 0px; padding: 0px; list-style: none; border: 0px; outline: none;\">&gt;&gt;&gt; Để nhận code, anh em bấm&nbsp;</b></font><a class=\"link-inline-content\" data-cke-saved-href=\"https://m.me/152595292913?ref=fb5f171788afa8a10dc58e9f90266eaa\" href=\"https://m.me/152595292913?ref=fb5f171788afa8a10dc58e9f90266eaa\" rel=\"nofollow\" target=\"_blank\" title=\"VÀO ĐÂY\" style=\"margin: 0px; padding: 0px; list-style: none; border: 0px; font-size: 15px; outline: none; font-family: &quot;Helvetica Neue&quot;, He