INSERT INTO `posts` VALUES (41, 'Chết cười với cách cảnh sát Ấn Độ sử dụng PUBG Mobile để giữ chân người dân ở trong nhà tránh dịch', 'Cảnh sát ở Mumbai đã chọn một biện pháp thú vị để khuyến khích mọi người ở nhà bằng PUBG Mobile.', '<p style=\"margin-bottom: 0px; padding: 0px 0px 20px; list-style: none; border: 0px; font-size: 17px; outline: none; color: rgb(51, 51, 51); font-family: &quot;Times New Roman&quot;; text-align: justify;\"><span style=\"margin: 0px; padding: 0px; list-style: none; border: 0px; outline: none;\"><a class=\"seo-suggest-link\" href=\"https://gamek.vn/phat-hien-ra-tham-hoa-chet-nguoi-cua-may-bay-trong-pubg-mobile-game-thu-khuyen-cao-nen-nhay-du-cang-som-cang-tot-20200421164529659.chn\" target=\"_blank\" title=\"Phát hiện ra thảm họa chết người của máy bay trong PUBG Mobile, game thủ khuyến cáo “nên nhảy dù càng sớm càng tốt”\" style=\"margin: 0px; padding: 0px; list-style: none; border: 0px; outline: none;\">PUBG Mobile</a>&nbsp;đã thay đổi ngành công nghiệp game ở Ấn Độ nhờ sự phổ biến của nó. Giờ đây tựa game này đã trở thành một phần không thể phủ nhận trong cuộc sống của hầu hết thanh niên ở Ấn Độ. Ngay cả cảnh sát cũng đã nhận ra tầm ảnh hưởng của trò chơi và bắt đầu sử dụng PUBG Mobile để khuyên mọi người ở trong nhà và&nbsp;</span><a class=\"link-inline-content\" data-cke-saved-href=\"http://Lộ hình ảnh những Anh Da Đen khiêng hòm sẽ \" dấn=\"\" thân\"=\"\" vào=\"\" vũ=\"\" trụ=\"\" của=\"\" liên=\"\" quân=\"\" mobile?\"=\"\" href=\"http://l%E1%BB%99%20h%C3%ACnh%20%E1%BA%A3nh%20nh%E1%BB%AFng%20anh%20da%20%C4%90en%20khi%C3%AAng%20h%C3%B2m%20s%E1%BA%BD/\" rel=\"nofollow\" target=\"_blank\" title=\"ngăn chặn sự lây lan của Covid-19.\" style=\"margin: 0px; padding: 0px; list-style: none; border: 0px; font-size: 15px; outline: none; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif;\">ngăn chặn sự lây lan của Covid-19.</a><br style=\"margin: 0px; padding: 0px; list-style: none;\"></p><div class=\"VCSortableInPreviewMod