INSERT INTO `posts` VALUES (55, 'Cứ nghĩ Blazes là người chơi tệ nhất trong trận CK VCS, hóa ra Levi mới là nguyên nhân thất bại của GAM?', 'Thần rừng VCS, đội trưởng của GAM Esports - Levi được cho là người có nhiều kinh nghiệm thi đấu nhất VCS hiện tại nhưng anh đã không làm tròn trách nhiệm với những danh hiệu vốn có của mình.', '<p style=\"margin-bottom: 25px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 17px; line-height: 25px; font-family: &quot;Times New Roman&quot;, Georgia, serif; vertical-align: baseline; color: rgb(34, 34, 34); -webkit-font-smoothing: subpixel-antialiased;\"><span style=\"margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline;\">Trận chung kết VCS Mùa Xuân 2020 đã khép lại với chức vô địch cực kỳ bất ngờ của tân vương Team Flash. Không chỉ hay về mặt cấm chọn mà những tình huống xử lý của đội tuyển được đánh giá thấp hơn đều tạo ra những bước ngoặt của trận chung kết. Ở chiều hướng ngược lại, GAM đã có 1 ngày thi đấu dưới sức. Nhìn vào KDA thì nhiều người đã tin rằng nguyên nhân chính khiến GAM trở thành cựu vương là vị trí xạ thủ của Blazes, nhưng liệu sự thật có phải vậy?</span><br></p><div class=\"VCSortableInPreviewMode active\" type=\"Photo\" style=\"margin: 0px auto 25px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 17px; line-height: inherit; font-family: &quot;Times New Roman&quot;, Georgia, serif; vertical-align: baseline; outline: 0px; text-align: center; width: 580px; color: rgb(34, 34, 34);\"><div style=\"margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline; outline: 0px;\"><a href=\"https://kenh14cdn.com/2020/4/23/anh1-15876447988221465308015.jpg\" data-fancybox-group=\"img-lightbox\" title=\"Blazes có một ngày thi đấu đáng quên với lượng sát thương ít ỏi trong cả 5 ván đấu\" target=\"_blank\" class=\"detail-img-lightbox\" style=\"margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline; cursor: zoom-in; display: block;\"><img src=\"https://kenh14cdn.com/thumb_w/620/2020/4/23/anh1-15876447988221465308015.jpg\" id=\"img_b0771b70-855d-11ea-b95f-f5944be05972\" w=\"750\" h=\"470\" alt=\"Cứ nghĩ Blazes là người chơi tệ nhất trong trận CK VCS, hóa ra Levi mới là nguyên nhân thất bại của GAM? - Ảnh 1.\" title=\"Cứ nghĩ Blazes là người chơi tệ nhất trong trận CK VCS, hóa ra Levi mới là nguyên nhân thất bại của GAM? - Ảnh 1.\" rel=\"lightbox\" photoid=\"b0771b70-855d-11ea-b95f-f5944be05972\" type=\"photo\" data-original=\"https://kenh14cdn.com/2020/4/23/anh1-15876447988221465308015.jpg\" width=\"\" height=\"\" class=\"lightbox-content\" style=\"margin: 0px; padding: 0px; font: inherit; vertical-align: bottom; color: transparent; display: block; width: 580px; max-width: 100%;\"></a></div><div class=\"PhotoCMS_Caption\" style=\"margin: 0px; padding: 10px; border: 0px; font-style: italic; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline; outline: 0px; background: rgb(242, 242, 242); text-align: left;\"><p data-placeholder=\"[nhập chú thích]\" style=\"padding: 0px; border: 0px; font-style: normal; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 18px; font-family: SFD-Medium; vertical-align: baseline; color: rgb(102, 102, 102); -webkit-font-smoothing: subpixel-antialiased; margin-bottom: 0px !important;\">Blazes có một ngày thi đấu đáng quên với lượng sát thương ít ỏi trong cả 5 ván đấu</p></div></div><p style=\"margin-bottom: 25px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 17px; line-height: 25px; font-family: &quot;Times New Roman&quot;, Georgia, serif; vertical-align: baseline; color: rgb(34, 34, 34); -webkit-font-smoothing: subpixel-antialiased;\">Nhưng khi so sánh 1 vấn đề thì chúng ta cần đặt trên cùng 1 hệ quy chiếu. Trong trận đấu với Team Flash, khi giai đoạn đi đường không thực sự quá chênh lệch thì những quyết định mang tính bước ngoặt tạo ra sự khác biệt. Hãy cùng phân tích về 3 ván thua của GAM để thấy rõ người thi đấu tệ nhất bên phía GAM không phải Blazes mà chính là cái tên được kỳ vọng nhất - Levi.</p><div class=\"VCSortableInPreviewMode active noCaption\" type=\"Photo\" style=\"margin: 0px auto 25px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 17px; line-height: inherit; font-family: &quot;Times New Roman&quot;, Georgia, serif; vertical-align: baseline; outline: 0px; text-align: center; width: 580px; color: rgb(34, 34, 34);\"><div style=\"margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline; outline: 0px;\"><a href=\"https://kenh14cdn.com/2020/4/23/anh2-1-1587645131517833211003.jpg\" data-fancybox-group=\"img-lightbox\" title=\"\" target=\"_blank\" class=\"detail-img-lightbox\" style=\"margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline; cursor: zoom-in; display: block;\"><img src=\"https://kenh14cdn.com/thumb_w/620/2020/4/23/anh2-1-1587645131517833211003.jpg\" id=\"img_75edf310-855e-11ea-96bd-a9658d11b879\" w=\"1000\" h=\"563\" alt=\"Cứ nghĩ Blazes là người chơi tệ nhất trong trận CK VCS, hóa ra Levi mới là nguyên nhân thất bại của GAM? - Ảnh 2.\" title=\"Cứ nghĩ Blazes là người chơi tệ nhất trong trận CK VCS, hóa ra Levi mới là nguyên nhân thất bại của GAM? - Ảnh 2.\" rel=\"lightbox\" photoid=\"75edf310-855e-11ea-96bd-a9658d11b879\" type=\"photo\" data-original=\"https://kenh14cdn.com/2020/4/23/anh2-1-1587645131517833211003.jpg\" width=\"\" height=\"\" class=\"lightbox-content\" style=\"margin: 0px; padding: 0px; font: inherit; vertical-align: bottom; color: transparent; display: block; width: 580px; max-width: 100%;\"></a></div></div><p style=\"margin-bottom: 25px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 17px; line-height: 25px; font-family: &quot;Times New Roman&quot;, Georgia, serif; vertical-align: baseline; color: rgb(34, 34, 34); -webkit-font-smoothing: subpixel-antialiased;\">GAM phiên bản 2020 là 1 tập thể cực mạnh. Họ mạnh đến nỗi vị trí xạ thủ có thể được đảm nhận bởi cái tên từng thi đấu ở đường giữa và vắng mặt hoàn toàn trong VCS Mùa Hè 2019 trước đó - Blazes. Công bằng mà nói, Blazes đi đường không tệ ở giai đoạn vòng bảng, thậm chí còn vượt quá sự mong đợi. Tỷ lệ các ván thua của GAM bị quyết định bởi việc Blazes thua đường là gần như không có, bởi lẽ Blazes đã thi đấu rất nỗ lực và Palette thì vẫn chứng tỏ đẳng cấp của mình.</p><p style=\"margin-bottom: 25px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 17px; line-height: 25px; font-family: &quot;Times New Roman&quot;, Georgia, serif; vertical-align: baseline; color: rgb(34, 34, 34); -webkit-font-smoothing: subpixel-antialiased;\"><span style=\"margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline;\">Levi không có được trạng thái tốt nhất ở kèo đấu Bo5 này. Ngay từ ván đấu đầu tiên, Levi cố gắng tìm kiếm lợi thế cho đường trên như mọi khi và bỏ trống đường dưới cho Flash tha hồ bắn phá. Trong những tình huống 2v4, trong đó bên kia có Galio thì cặp đôi đường dưới của GAM chẳng biết phải làm gì. Rất nhiều tình huống kêu gọi giao tranh được cho là được call tới từ vị trí của Levi. Một trong số đó là sự sai lầm ở tình huống bước ngoặt phút 22, khi GAM cố gắng quay lại giao tranh 4vs4 lúc Blazes không còn quá nhiều máu và năng lượng. Hệ quả là GAM bị quét sạch và đội hình Flash dễ dàng đè bẹp đối thủ ở các pha giao tranh tiếp theo.</span></p><div class=\"VCSortableInPreviewMode active\" type=\"Photo\" style=\"margin: 0px auto 25px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 17px; line-height: inherit; font-family: &quot;Times New Roman&quot;, Georgia, serif; vertical-align: baseline; outline: 0px; text-align: center; width: 580px; color: rgb(34, 34, 34);\"><div style=\"margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline; outline: 0px;\"><a href=\"https://kenh14cdn.com/2020/4/23/anh3-1587645191565812627736.jpg\" data-fancybox-group=\"img-lightbox\" title=\"Trong khi Levi “mất hút\" thì=\"\" yijin=\"\" có=\"\" ngày=\"\" thi=\"\" đấu=\"\" bùng=\"\" nổ\"=\"\" target=\"_blank\" class=\"detail-img-lightbox\" style=\"margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline; cursor: zoom-in; display: block;\"><img src=\"https://kenh14cdn.com/thumb_w/620/2020/4/23/anh3-1587645191565812627736.jpg\" id=\"img_9b2eff70-855e-11ea-94cc-f9908238caa6\" w=\"2000\" h=\"1333\" alt=\"Cứ nghĩ Blazes là người chơi tệ nhất trong trận CK VCS, hóa ra Levi mới là nguyên nhân thất bại của GAM? - Ảnh 4.\" title=\"Cứ nghĩ Blazes là người chơi tệ nhất trong trận CK VCS, hóa ra Levi mới là nguyên nhân thất bại của GAM? - Ảnh 4.\" rel=\"lightbox\" photoid=\"9b2eff70-855e-11ea-94cc-f9908238caa6\" type=\"photo\" data-original=\"https://kenh14cdn.com/2020/4/23/anh3-1587645191565812627736.jpg\" width=\"\" height=\"\" class=\"lightbox-content\" style=\"margin: 0px; padding: 0px; font: inherit; vertical-align: bottom; color: transparent; display: block; width: 580px; max-width: 100%;\"></a></div><div class=\"PhotoCMS_Caption\" style=\"margin: 0px; padding: 10px; border: 0px; font-style: italic; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline; outline: 0px; background: rgb(242, 242, 242); text-align: left;\"><p data-placeholder=\"[nhập chú thích]\" style=\"padding: 0px; border: 0px; font-style: normal; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 18px; font-family: SFD-Medium; vertical-align: baseline; color: rgb(102, 102, 102); -webkit-font-smoothing: subpixel-antialiased; margin-bottom: 0px !important;\">Trong khi Levi “mất hút\" thì YiJin có ngày thi đấu bùng nổ</p></div></div><div id=\"admzone474524\" class=\"wp100 clearfix\" style=\"margin: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 17px; line-height: inherit; font-family: &quot;Times New Roman&quot;, Georgia, serif; vertical-align: baseline; outline: 0px; width: 580px; color: rgb(34, 34, 34);\"><div id=\"zone-474524\" style=\"margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline; outline: 0px;\"><div id=\"share-jvrit1l1\" style=\"margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline; outline: 0px;\"><div id=\"placement-566957\" revenue=\"cpm\" style=\"margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline; outline: 0px;\"><div id=\"banner-474524-570242\" style=\"margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline; outline: 0px; min-height: 0px; min-width: 0px;\"><div id=\"slot-1-474524-570242\" style=\"margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline; outline: 0px;\"><div id=\"ssppagebid_3476\" style=\"margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline; outline: 0px;\"><div id=\"sspbid_2011776\" data-ssp=\"sspbid_2011776\" class=\"banner0\" data-admssprqid=\"7abcc818-3d34-45c5-8037-f2822e6e9a85-5ea5340b\" data-width=\"undefined\" data-height=\"undefined\" style=\"margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline; outline: 0px;\"><iframe id=\"ads-place-2011776\" scrolling=\"no\" width=\"0\" height=\"0\" style=\"margin: 0px; padding: 0px; border-width: 0px; border-style: initial; font: inherit; vertical-align: baseline; max-width: 100%;\"></iframe></div></div></div></div></div></div></div></div><p style=\"margin-bottom: 25px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 17px; line-height: 25px; font-family: &quot;Times New Roman&quot;, Georgia, serif; vertical-align: baseline; color: rgb(34, 34, 34); -webkit-font-smoothing: subpixel-antialiased;\">Trong ván 3, Levi mắc sai lầm chí mạng khi tính toán sát thương lỗi ở pha băng trụ cùng Kiaya ngay những phút đầu tiên. Nhiều người cho rằng GAM trong ván đấu này thiếu may mắn ở tình huống Blazes không thể đi theo đồng đội dù đứng trong chiêu cuối của Ryze ở phút thứ 27. Nhưng điều đó sẽ không thể xảy ra nếu Levi đủ tỉnh táo ở những pha xử lý đầu trận. Trong khi Kiaya và Dia1 liên tục bị Reksai của YiJin hỏi thăm thì Lee Sin của Levi hoàn toàn mất tích trên bản đồ. K/D/A tệ hại 0-6-4, thua rừng đối thủ 6000 tiền có lẽ là màn trình diễn tệ hại nhất trong mùa giải của thần rừng VCS.</p><div class=\"VCSortableInPreviewMode noCaption\" type=\"Photo\" style=\"margin: 0px auto 25px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 17px; line-height: inherit; font-family: &quot;Times New Roman&quot;, Georgia, serif; vertical-align: baseline; outline: 0px; text-align: center; width: 580px; color: rgb(34, 34, 34);\"><div style=\"margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline; outline: 0px;\"><a href=\"https://kenh14cdn.com/2020/4/23/anh4-15876452106631403005302.png\" data-fancybox-group=\"img-lightbox\" title=\"\" target=\"_blank\" class=\"detail-img-lightbox\" style=\"margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline; cursor: zoom-in; display: block;\"><img src=\"https://kenh14cdn.com/thumb_w/620/2020/4/23/anh4-15876452106631403005302.png\" id=\"img_a5802440-855e-11ea-8ab8-47fb8602be3f\" w=\"1606\" h=\"120\" alt=\"Cứ nghĩ Blazes là người chơi tệ nhất trong trận CK VCS, hóa ra Levi mới là nguyên nhân thất bại của GAM? - Ảnh 5.\" title=\"Cứ nghĩ Blazes là người chơi tệ nhất trong trận CK VCS, hóa ra Levi mới là nguyên nhân thất bại của GAM? - Ảnh 5.\" rel=\"lightbox\" photoid=\"a5802440-855e-11ea-8ab8-47fb8602be3f\" type=\"photo\" data-original=\"https://kenh14cdn.com/2020/4/23/anh4-15876452106631403005302.png\" width=\"\" height=\"\" class=\"lightbox-content\" style=\"margin: 0px; padding: 0px; font: inherit; vertical-align: bottom; color: transparent; display: block; width: 580px; max-width: 100%;\"></a></div></div><p style=\"margin-bottom: 25px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 17px; line-height: 25px; font-family: &quot;Times New Roman&quot;, Georgia, serif; vertical-align: baseline; color: rgb(34, 34, 34); -webkit-font-smoothing: subpixel-antialiased;\"><br></p><div class=\"VCSortableInPreviewMode noCaption\" type=\"Photo\" style=\"margin: 0px auto 25px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 17px; line-height: inherit; font-family: &quot;Times New Roman&quot;, Georgia, serif; vertical-align: baseline; outline: 0px; text-align: center; width: 580px; color: rgb(34, 34, 34);\"><div style=\"margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline; outline: 0px;\"><a href=\"https://kenh14cdn.com/2020/4/23/anh5-15876452213671306794091.png\" data-fancybox-group=\"img-lightbox\" title=\"\" target=\"_blank\" class=\"detail-img-lightbox\" style=\"margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline; cursor: zoom-in; display: block;\"><img src=\"https://kenh14cdn.com/thumb_w/620/2020/4/23/anh5-15876452213671306794091.png\" id=\"img_abf45d00-855e-11ea-8ab8-47fb8602be3f\" w=\"1592\" h=\"102\" alt=\"Cứ nghĩ Blazes là người chơi tệ nhất trong trận CK VCS, hóa ra Levi mới là nguyên nhân thất bại của GAM? - Ảnh 6.\" title=\"Cứ nghĩ Blazes là người chơi tệ nhất trong trận CK VCS, hóa ra Levi mới là nguyên nhân thất bại của GAM? - Ảnh 6.\" rel=\"lightbox\" photoid=\"abf45d00-855e-11ea-8ab8-47fb8602be3f\" type=\"photo\" data-original=\"https://kenh14cdn.com/2020/4/23/anh5-15876452213671306794091.png\" width=\"\" height=\"\" class=\"lightbox-content\" style=\"margin: 0px; padding: 0px; font: inherit; vertical-align: bottom; color: transparent; display: block; width: 580px; max-width: 100%;\"></a></div></div><p style=\"margin-bottom: 25px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 17px; line-height: 25px; font-family: &quot;Times New Roman&quot;, Georgia, serif; vertical-align: baseline; color: rgb(34, 34, 34); -webkit-font-smoothing: subpixel-antialiased;\">Trong ván đấu quyết định, GAM mắc bẫy cấm chọn của đối thủ còn Levi thì đúng là bất lực trước combo chết chóc Skarner - Taric bên phía Team Flash. GAM đã có thời điểm dẫn trước Flash 5 nghìn tiền và có hàng tá lợi thế nhưng bởi Shyvana của Levi không quá vượt trội so với Skarner của YiJin nên các tình huống giao tranh ở thế thua vẫn giúp Flash có hàng loạt điểm hạ gục. Đỉnh điểm của sự tan vỡ chính là việc Levi bất lực bị đối thủ hạ gục ở pha giao tranh quyết định phút thứ 33.</p><div class=\"VCSortableInPreviewMode active\" type=\"Photo\" style=\"margin: 0px auto 25px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 17px; line-height: inherit; font-family: &quot;Times New Roman&quot;, Georgia, serif; vertical-align: baseline; outline: 0px; text-align: center; width: 580px; color: rgb(34, 34, 34);\"><div style=\"margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline; outline: 0px;\"><a href=\"https://kenh14cdn.com/2020/4/23/sss-1587645339270521132344.jpg\" data-fancybox-group=\"img-lightbox\" title=\"Levi \" bốc=\"\" hơi\"=\"\" ngay=\"\" sau=\"\" cú=\"\" r=\"\" của=\"\" yijin=\"\" mà=\"\" không=\"\" có=\"\" khăn=\"\" giải=\"\" thuật=\"\" hay=\"\" hòm=\"\" bảo=\"\" hộ\"=\"\" target=\"_blank\" class=\"detail-img-lightbox\" style=\"margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline; cursor: zoom-in; display: block;\"><img src=\"https://kenh14cdn.com/thumb_w/620/2020/4/23/sss-1587645339270521132344.jpg\" id=\"img_f28f5080-855e-11ea-be26-49c1e3c8cf70\" w=\"1920\" h=\"1080\" alt=\"Cứ nghĩ Blazes là người chơi tệ nhất trong trận CK VCS, hóa ra Levi mới là nguyên nhân thất bại của GAM? - Ảnh 7.\" title=\"Cứ nghĩ Blazes là người chơi tệ nhất trong trận CK VCS, hóa ra Levi mới là nguyên nhân thất bại của GAM? - Ảnh 7.\" rel=\"lightbox\" photoid=\"f28f5080-855e-11ea-be26-49c1e3c8cf70\" type=\"photo\" data-original=\"https://kenh14cdn.com/2020/4/23/sss-1587645339270521132344.jpg\" width=\"\" height=\"\" class=\"lightbox-content\" style=\"margin: 0px; padding: 0px; font: inherit; vertical-align: bottom; color: transparent; display: block; width: 580px; max-width: 100%;\"></a></div><div class=\"PhotoCMS_Caption\" style=\"margin: 0px; padding: 10px; border: 0px; font-style: italic; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline; outline: 0px; background: rgb(242, 242, 242); text-align: left;\"><p data-placeholder=\"[nhập chú thích]\" style=\"padding: 0px; border: 0px; font-style: normal; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 18px; font-family: SFD-Medium; vertical-align: baseline; color: rgb(102, 102, 102); -webkit-font-smoothing: subpixel-antialiased; margin-bottom: 0px !important;\">Levi \"bốc hơi\" ngay sau cú R của Yijin mà không có Khăn Giải Thuật hay Hòm Bảo Hộ</p></div></div><p style=\"margin-bottom: 25px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 17px; line-height: 25px; font-family: &quot;Times New Roman&quot;, Georgia, serif; vertical-align: baseline; color: rgb(34, 34, 34); -webkit-font-smoothing: subpixel-antialiased;\">Levi và GAM bỏ qua thời cơ đánh bại đối thủ một phần bởi cách đi rừng quá chú trọng farm rừng của anh chàng này. Thậm chí điều đó khiến các chủ lực của GAM không có đủ lượng trang bị cần thiết bởi Levi đã dọn sạch bãi quái rừng (Chỉ số lính của Levi trong ván 5 thậm chí chỉ kém mỗi Blazes). Trong 1 trò chơi mà lượng tài nguyên có giới hạn, Levi đã mắc sai lầm toàn tập khi tự biến mình trở thành người có lượng trang bị tốt nhất bên phía GAM rồi chẳng làm được gì khi bị đối thủ tập trung. Với một loạt sai lầm như vậy thì GAM thua cũng là lẽ tất yếu.</p><div class=\"VCSortableInPreviewMode active\" type=\"Photo\" style=\"margin: 0px auto 25px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 17px; line-height: inherit; font-family: &quot;Times New Roman&quot;, Georgia, serif; vertical-align: baseline; outline: 0px; text-align: center; width: 580px; color: rgb(34, 34, 34);\"><div style=\"margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline; outline: 0px;\"><a href=\"https://kenh14cdn.com/2020/4/23/anh6-1-15876452415502118090361.jpg\" data-fancybox-group=\"img-lightbox\" title=\"GAM còn rất nhiều việc phải làm, trong đó Levi cũng cần cải thiện phong độ\" target=\"_blank\" class=\"detail-img-lightbox\" style=\"margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline; cursor: zoom-in; display: block;\"><img src=\"https://kenh14cdn.com/thumb_w/620/2020/4/23/anh6-1-15876452415502118090361.jpg\" id=\"img_b7a55ff0-855e-11ea-8bbb-89c380aeaf80\" w=\"800\" h=\"450\" alt=\"Cứ nghĩ Blazes là người chơi tệ nhất trong trận CK VCS, hóa ra Levi mới là nguyên nhân thất bại của GAM? - Ảnh 8.\" title=\"Cứ nghĩ Blazes là người chơi tệ nhất trong trận CK VCS, hóa ra Levi mới là nguyên nhân thất bại của GAM? - Ảnh 8.\" rel=\"lightbox\" photoid=\"b7a55ff0-855e-11ea-8bbb-89c380aeaf80\" type=\"photo\" data-original=\"https://kenh14cdn.com/2020/4/23/anh6-1-15876452415502118090361.jpg\" width=\"\" height=\"\" class=\"lightbox-content\" style=\"margin: 0px; padding: 0px; font: inherit; vertical-align: bottom; color: transparent; display: block; width: 580px; max-width: 100%;\"></a></div><div class=\"PhotoCMS_Caption\" style=\"margin: 0px; padding: 10px; border: 0px; font-style: italic; font-variant: inherit; font-weight: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline; outline: 0px; background: rgb(242, 242, 242); text-align: left;\"><p data-placeholder=\"[nhập chú thích]\" style=\"padding: 0px; border: 0px; font-style: normal; font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 14px; line-height: 18px; font-family: SFD-Medium; vertical-align: baseline; color: rgb(102, 102, 102); -webkit-font-smoothing: subpixel-antialiased; margin-bottom: 0px !important;\">GAM còn rất nhiều việc phải làm, trong đó Levi cũng cần cải thiện phong độ</p></div></div><p style=\"margin-bottom: 25px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 17px; line-height: 25px; font-family: &quot;Times New Roman&quot;, Georgia, serif; vertical-align: baseline; color: rgb(34, 34, 34); -webkit-font-smoothing: subpixel-antialiased;\">Blazes có thể thi đấu không tốt trong cặp đấu Bo5 vừa qua, nhưng trách nhiệm từ những quyết định đi rừng của Levi cần phải được nhìn nhận 1 cách khách quan. YiJin thi đấu rất đồng đội trong khi Levi thì lại không có được điều đó. Khi đã tham dự hàng loạt sự kiện quốc tế lớn và tạo ra ấn tượng mạnh cho cộng đồng LMHT quốc tế, Levi có lẽ sẽ hối tiếc vì rất nhiều tình huống mà anh có thể làm tốt hơn. Levi sẽ còn VCS Mùa Hè 2020 để làm lại từ đầu, và GAM cần 1 sự nhất quán trong lối chơi thay vì sự chủ quan đến như vậy trong 1 trận chung kết.</p><div class=\"VCSortableInPreviewMode noCaption active\" type=\"Photo\" style=\"margin: 0px auto 25px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 17px; line-height: inherit; font-family: &quot;Times New Roman&quot;, Georgia, serif; vertical-align: baseline; outline: 0px; text-align: center; width: 580px; color: rgb(34, 34, 34);\"><div style=\"margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline; outline: 0px;\"><a class=\"link-callout\" href=\"https://kenh14.vn/esports.chn\" target=\"_blank\" style=\"margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline;\"><img src=\"https://kenh14cdn.com/thumb_w/620/2019/11/22/kenh-14-footer-2-1574402453493678167674.png\" id=\"img_720dc930-0ced-11ea-b97a-a15b28c7af0e\" w=\"1500\" h=\"500\" alt=\"Cứ nghĩ Blazes là người chơi tệ nhất trong trận CK VCS, hóa ra Levi mới là nguyên nhân thất bại của GAM? - Ảnh 9.\" title=\"Cứ nghĩ Blazes là người chơi tệ nhất trong trận CK VCS, hóa ra Levi mới là nguyên nhân thất bại của GAM? - Ảnh 9.\" rel=\"lightbox\" photoid=\"720dc930-0ced-11ea-b97a-a15b28c7af0e\" type=\"photo\" data-original=\"https://kenh14cdn.com/2019/11/22/kenh-14-footer-2-1574402453493678167674.png\" data-mobile-url=\"https://kenh14cdn.com/2019/11/22/kenh-14-footer-1-15744058540261916280742.png\" data-mobile-width=\"1036\" data-mobile-height=\"963\" width=\"\" height=\"\" class=\"lightbox-content\" style=\"margin: 0px; padding: 0px; font: inherit; vertical-align: bottom; color: transparent; max-width: 100%; width: 580px;\"></a></div></div><div class=\"VCSortableInPreviewMode noCaption active\" type=\"Photo\" style=\"margin: 0px auto 25px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 17px; line-height: inherit; font-family: &quot;Times New Roman&quot;, Georgia, serif; vertical-align: baseline; outline: 0px; text-align: center; width: 580px; color: rgb(34, 34, 34);\"></div>', 'post_lmht6.jpg', 2, 7, 1, '2020-04-26 14:14:25', 0);
INSERT INTO `posts` VALUES (56, 'Liên Quân Mobile: Garena tặng Giftcode cả server, game thủ hồ hởi khoe trúng skin SS và SSS', 'Game thủ Liên Quân Mobile đã và đang \"troll\" lẫn nhau không hề nhẹ.', '<p style=\"margin-bottom: 25px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 17px; line-height: 25px; font-family: &quot;Times New Roman&quot;, Georgia, serif; vertical-align: baseline; color: rgb(34, 34, 34); -webkit-font-smoothing: subpixel-antialiased;\">Tháng 5 tới đây là khoảng thời gian mà game thủ Liên Quân Mobile lại được ưu đãi vô vàn quà tặng ingame hấp dẫn. Đơn giản là bởi cộng đồng hướng tới chuỗi sự kiện mừng ngày 30/4 - 1/5 và xa hơn nữa là ngày Arena of Valor thế giới (5/5).</p><p style=\"margin-bottom: 25px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 17px; line-height: 25px; font-family: &quot;Times New Roman&quot;, Georgia, serif; vertical-align: baseline; color: rgb(34, 34, 34); -webkit-font-smoothing: subpixel-antialiased;\">Khả năng cao là các NPH Liên Quân Mobile sẽ lại tung ra những sự kiện tặng miễn phí tướng, phục trang các loại. Khởi đầu cho chuỗi event sôi động đó thì Garena lúc này đã triển khai sớm đợt tặng Giftcode cho game thủ Việt. Mã giftcode đó là&nbsp;<b style=\"margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline;\">BATHZBZRH4VQ</b>.</p><div class=\"VCSortableInPreviewMode\" type=\"Photo\" style=\"margin: 0px auto 25px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 17px; line-height: inherit; font-family: &quot;Times New Roman&quot;, Georgia, serif; vertical-align: baseline; outline: 0px; text-align: center; width: 580px; color: rgb(34, 34, 34);\"><div st